00311         public void SortSensor()
00312         {
00313             var sensorData = new List<(string Label, double Value)>();
00314             foreach (DataGridViewRow row in dataGridViewSensor.Rows)
00315             {
00316                 if (row.Cells["SensorValue"].Value != null)
00317                 {
00318                     sensorData.Add((row.Cells["SensorLabel"].Value.ToString(), Convert.ToDouble(row.Cells["SensorValue"].Value)));
00319                 }
00320             }
00321             for (int i = 0; i < sensorData.Count - 1; i++)
00322             {
00323                 for (int j = 0; j < sensorData.Count - i - 1; j++)
00324                 {
00325                     if (sensorData[j].Value > sensorData[j + 1].Value)
00326                     {
00327                         var temp = sensorData[j];
00328                         sensorData[j] = sensorData[j + 1];
00329                         sensorData[j + 1] = temp;
00330                     }
00331                 }
00332             }
00333             dataGridViewSensor.Rows.Clear();
00334             foreach (var data in sensorData)
00335             {
00336                 dataGridViewSensor.Rows.Add(data.Label, data.Value);
00337             }
00338             sorted = true;
00339             MessageBox.Show("Data sorted in ascending order.", "Sort Data");
00340         }
