00386         public void FilterData()
00387         {
00388             double max, min;
00389             if (!double.TryParse(txtBoxUpper.Text, out max) ||
00390                 !double.TryParse(txtBoxLower.Text, out min))
00391             {
00392                 MessageBox.Show("Please enter valid numeric values for max and min.", "Input Error",
00393                                 MessageBoxButtons.OK, MessageBoxIcon.Warning);
00394                 return;
00395             }
00396             if (min >= max)
00397             {
00398                 MessageBox.Show("Minimum value must be less than maximum value.", "Input Error",
00399                                 MessageBoxButtons.OK, MessageBoxIcon.Warning);
00400                 return;
00401             }
00402             else if (min < 0 || max < 0)
00403             {
00404                 MessageBox.Show("Values must be non-negative.", "Input Error",
00405                                 MessageBoxButtons.OK, MessageBoxIcon.Warning);
00406                 return;
00407             }
00408             foreach (DataGridViewRow row in dataGridViewSensor.Rows)
00409             {
00410                 double value = Convert.ToDouble(row.Cells["SensorValue"].Value);
00411                 if (value >= max)
00412                 {
00413                     row.DefaultCellStyle.BackColor = Color.Red;
00414                 }
00415                 else if (value <= min)
00416                 {
00417                     row.DefaultCellStyle.BackColor = Color.LightBlue;
00418                 }
00419                 else
00420                 {
00421                     row.DefaultCellStyle.BackColor = Color.Green;
00422                 }
00423             }
00424             txtBoxUpper.Text = max.ToString();
00425             txtBoxLower.Text = min.ToString();
00426         }
00431         public void LoadFile(string filePath)
00432         {
00433             dataGridViewSensor.Rows.Clear();
00434             string extension = Path.GetExtension(filePath).ToLower();
00435             if (extension == ".bin")
00436             {
00437                 using (var reader = new BinaryReader(File.Open(filePath, FileMode.Open)))
00438                 {
00439                     while (reader.BaseStream.Position != reader.BaseStream.Length)
00440                     {
00441                         string label = reader.ReadString();
00442                         double value = reader.ReadDouble();
00443                         dataGridViewSensor.Rows.Add(label, value);
00444                     }
00445                 }
00446             }
00447             else if (extension == ".csv")
00448             {
00449                 var lines = File.ReadAllLines(filePath);
00450                 foreach (var line in lines)
00451                 {
00452                     var parts = line.Split(',');
00453                     if (parts.Length == 2 && double.TryParse(parts[1], out double value))
00454                         dataGridViewSensor.Rows.Add(parts[0], value);
00455                 }
00456             }
00457             comboBoxListOfFile.Text = Path.GetFileName(filePath);
00458             sorted = false;
00459         }
