00494         public void SaveFileFunction(string fileName)
00495         {
00496             bool isEmpty = dataGridViewSensor.Rows.Count == 0;
00497             if (isEmpty)
00498             {
00499                 MessageBox.Show("No data available to save.", "No Data",
00500                                 MessageBoxButtons.OK, MessageBoxIcon.Information);
00501                 return;
00502             }
00503             using (SaveFileDialog saveFileDialog = new SaveFileDialog())
00504             {
00505                 saveFileDialog.Filter = "Binary files (*.bin)|*.bin|CSV files (*.csv)|*.csv|All files (*.*)|*.*";
00506                 if (string.IsNullOrEmpty(currentDirectory) || !Directory.Exists(currentDirectory))
00507                 {
00508                     currentDirectory = "C:\\Sensing4U\\DataStorage\\";
00509                 }
00510                 saveFileDialog.InitialDirectory = currentDirectory;
00511                 if (!string.IsNullOrEmpty(fileName))
00512                 {
00513                     saveFileDialog.FileName = Path.GetFileName(fileName);
00514                 }
00515                 if (saveFileDialog.ShowDialog() == DialogResult.OK)
00516                 {
00517                     try
00518                     {
00519                         string extension = Path.GetExtension(saveFileDialog.FileName).ToLower();
00520                         if (extension == ".bin")
00521                         {
00522                             using (var writer = new BinaryWriter(File.Open(saveFileDialog.FileName, FileMode.Create)))
00523                             {
00524                                 foreach (DataGridViewRow row in dataGridViewSensor.Rows)
00525                                 {
00526                                     writer.Write(row.Cells["SensorLabel"].Value.ToString());
00527                                     writer.Write(Convert.ToDouble(row.Cells["SensorValue"].Value));
00528                                 }
00529                             }
00530                         }
00531                         else if (extension == ".csv")
00532                         {
00533                             using (var writer = new StreamWriter(saveFileDialog.FileName))
00534                             {
00535                                 foreach (DataGridViewRow row in dataGridViewSensor.Rows)
00536                                 {
00537                                     writer.WriteLine($"{row.Cells["SensorLabel"].Value},{row.Cells["SensorValue"].Value}");
00538                                 }
00539                             }
00540                         }
00541                         MessageBox.Show("File saved successfully.", "Save File");
00542                     }
00543                     catch (Exception ex)
00544                     {
00545                         MessageBox.Show($"Error saving file: {ex.Message}", "Save Error",
00546                                         MessageBoxButtons.OK, MessageBoxIcon.Error);
00547                     }
00548                 }
00549             }
00550         }
