00584         public int BinarySearch(double targetValue)
00585         {
00586             var sensorData = new List<(string Label, double Value)>();
00587 
00588             // Sort data if not sorted
00589             if (!sorted)
00590                 SortSensor();
00591             double searchValue = targetValue;
00592             // Extract grid data
00593             foreach (DataGridViewRow row in dataGridViewSensor.Rows)
00594             {
00595                 if (row.Cells["SensorValue"].Value != null)
00596                 {
00597                     sensorData.Add((row.Cells["SensorLabel"].Value.ToString(),
00598                                     Convert.ToDouble(row.Cells["SensorValue"].Value)));
00599                 }
00600             }
00601             int left = 0;
00602             int right = sensorData.Count - 1;
00603             int foundIndex = -1;
00604             // Binary Search
00605             while (left <= right)
00606             {
00607                 int mid = left + (right - left) / 2;
00608                 if (sensorData[mid].Value == searchValue)
00609                 {
00610                     foundIndex = mid;
00611                     break;
00612                 }
00613 
00614                 if (sensorData[mid].Value < searchValue)
00615                     left = mid + 1;
00616                 else
00617                     right = mid - 1;
00618             }
00619 
00620             // No match found
00621             if (foundIndex == -1)
00622             {
00623                 MessageBox.Show("Value not found in the data.", "Search Result");
00624                 return -1;
00625             }
00626 
00627             // Highlight ALL rows with this value
00628             foreach (DataGridViewRow row in dataGridViewSensor.Rows)
00629             {
00630                 double value = Convert.ToDouble(row.Cells["SensorValue"].Value);
00631                 if (value == searchValue)
00632                     row.Cells["SensorValue"].Style.BackColor = Color.Yellow;
00633             }
00634 
00635             MessageBox.Show($"Value {searchValue} found (highlighted in yellow).", "Search Result");
00636             return foundIndex;
00637         }
